import React from 'react';
import PropTypes from 'prop-types';
import { css } from 'emotion';
import Button from './InitialScreen';

const propTypes = {
  showResult: PropTypes.object,
};

const ResultsScreen = ({ showResult }) => {
  return (
    <div className={styles.root}>
      <Button
        classes={{
          root: styles.button({
            color: showResult.color,
          })
        }}
        variant="contained"
        startIcon={<img src={showResult.src} alt='hand'/>}
      />
    </div>
  );
};

const styles = {
  root: css({
    width: '100%'
  }),
  button: ({ color, withDisplayBlock, withMarginRight }) => css({
    '&.MuiButton-root': {
      width: 200,
      height: 200,
      borderRadius: '50%',
      display: withDisplayBlock ? 'block' : 'inline',
      backgroundColor: 'white',
      border: `20px solid ${color}`,
      margin: 'auto',
      marginRight: withMarginRight ? 24 : 'auto'
    }
  })
};

ResultsScreen.propTypes = propTypes;

export default ResultsScreen;
