import React from 'react';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import { css } from 'emotion';
import rock from '../images/icon-rock.svg';
import paper from '../images/icon-paper.svg';
import scissors from '../images/icon-scissors.svg';

const propTypes = {
  setShowResult: PropTypes.func,
};

const buttons = [
  {
    color: 'red',
    src: rock,
  },
  {
    color: 'yellow',
    src: paper,
  },
  {
    color: 'blue',
    src: scissors,
  }
];

const InitialScreen = ({ setResult }) => {

  console.log('setShowRsetResultesult', setResult)
  return (
    <div className={styles.root}>
      {
        buttons.map(button => {
          return (
            <>
              <Button
                onClick={() => setResult({
                  src: button.src,
                  color: button.color
                })}
                key={button.src}
                classes={{
                  root: styles.button({
                    color: button.color,
                    withDisplayBlock: button.color === 'blue',
                    withMarginRight: button.color === 'red'
                  })
                }}
                variant="contained"
                startIcon={<img src={button.src} alt='hand'/>}
              />
            </>
          );
        })
      }
    </div>
  );
};

const styles = {
  root: css({
    width: '100%'
  }),
  button: ({ color, withDisplayBlock, withMarginRight }) => css({
    '&.MuiButton-root': {
      width: 200,
      height: 200,
      borderRadius: '50%',
      display: withDisplayBlock ? 'block' : 'inline',
      backgroundColor: 'white',
      border: `20px solid ${color}`,
      margin: 'auto',
      marginRight: withMarginRight ? 24 : 'auto'
    }
  })
};

InitialScreen.propTypes = propTypes;

export default InitialScreen;
