import React from 'react';
import PropTypes from 'prop-types';
import { css } from 'emotion';
import Button from '@material-ui/core/Button';

const propTypes = {
  showResult: PropTypes.object,
  setShowResult: PropTypes.func,
  resultType: PropTypes.string,
};

const ResultsScreen = ({ showResult, setShowResult, resultType }) => {
  return (
    <div className={styles.root}>
      {/*<OwnHand showResult={showResult} />*/}
      <Button
        classes={{
          root: styles.button({
            color: showResult.color,
          })
        }}
        variant="contained"
        startIcon={<img src={showResult.src} alt='hand'/>}
      />
      <div className={styles.flexCol}>
        <span className={styles.resultText}> {resultType} </span>
        <Button
          onClick={() => setShowResult({
            src: null,
            color: null
          })}
          classes={{
            root: styles.buttonRestart
          }}
          variant="contained"
        > Play Again
        </Button>
      </div>
    </div>
  );
};

const styles = {
  root: css({
    width: '100%',
    display: 'flex',
    alignItems: 'center'
  }),
  button: ({ color, withDisplayBlock, withMarginRight }) => css({
    '&.MuiButton-root': {
      width: 200,
      height: 200,
      borderRadius: '50%',
      display: withDisplayBlock ? 'block' : 'inline',
      backgroundColor: 'white',
      border: `20px solid ${color}`,
      marginRight: withMarginRight ? 24 : 'auto'
    }
  }),
  buttonRestart: css({
    '&.MuiButton-root': {
      width: 200,
      height: 40,
      borderRadius: 8,
      backgroundColor: 'white',
      color: 'black'
    }
  }),
  flexCol: css({
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  }),
  resultText: css({
    color: 'white',
    fontSize: 32,
    textTransform: 'uppercase',
  })
};

ResultsScreen.propTypes = propTypes;

export default ResultsScreen;
